import { formatISO9075 } from 'date-fns';
import { Link } from 'react-router-dom';

export default function Post({
  _id,
  title,
  summary,
  cover,
  content,
  createdAt,
  author,
}) {
  return (
    <>
      <div className="post">
        <div className="image">
          <img
            src={
              'http://localhost:4000/uploads/4e865cb0aef51ef6b86002cbb8f45fbf.webp'
            }
            alt=""
          />
        </div>
        <div className="texts">
          <h2>{title}</h2>
          <p className="info">
            <a className="author">{author.userName}</a>
            <time>
              {formatISO9075(new Date(createdAt), 'MMM d, yyyy HH:mm')}
            </time>
          </p>
          <p className="summary">{summary}</p>
        </div>
      </div>
    </>
  );
}
