import { formatISO9075 } from 'date-fns';

export default function Post({
  _id,
  title,
  summary,
  cover,
  content,
  createdAt,
}) {
  return (
    <>
      <div className="post">
        <div className="image">
          <img src={cover} alt="lol" />
        </div>
        <div className="texts">
          <h2>{title}</h2>
          <p className="info">
            <a className="author">Shreyansh</a>
            <time>
              {formatISO9075(new Date(createdAt), 'MMM d, yyyy HH:mm')}
            </time>
          </p>
          <p className="summary">{summary}</p>
        </div>
      </div>
    </>
  );
}
